import React, { Component } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";

export default class News extends Component {
    constructor(){
        super();
        this.state ={
            articles : [],
            loading : false,
            page : 1
        }
    }

   async componentDidMount(){
        let url =`https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=4286c5491ec44d3d8502db05fe5d246c&page=1&pageSize=${this.props.pageSize}`;
        this.setState({loading: true})
        let data = await fetch(url);
        let parsedata = await data.json();
        console.log(parsedata);
        this.setState({articles : parsedata.articles,
            totalResults : parsedata.totalResults,
            loading: false
        })
    }

    handlePrevBTn = async()=>{
        let url =`https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=4286c5491ec44d3d8502db05fe5d246c&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`;
        this.setState({loading: true})
        let data = await fetch(url);
        let parsedata = await data.json();
        console.log(parsedata);
        this.setState({articles : parsedata.articles,
                       page : this.state.page - 1,
                       loading: false
        
        })
}
    handleNextBTn = async()=>{
        if (!(this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize)))
        this.setState({loading: true})
            let url =`https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=4286c5491ec44d3d8502db05fe5d246c&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
        let data = await fetch(url);
        let parsedata = await data.json();
        console.log(parsedata);
        this.setState({articles : parsedata.articles,
                       page : this.state.page + 1,
                       loading: false
        }) 
    }


  render() {
    return (
        <div className="container my-3">
            <h1>NewsMonkey - Top Headlines</h1>
            {this.state.loading && <Spinner/>}
                <div className="row">
                    {!this.state.loading && this.state.articles.map((element)=>{
                    return  <div className="col-md-4" key={element.url}>
                    <NewsItem title={element.title} description={element.description} imageUrl={element.urlToImage} newsurl={element.url}/>
                    </div>
                    })};
                </div>
           <div className="container d-flex justify-content-between">
                <button disabled={this.state.page <= 1} type="button" className="btn btn-dark" onClick={this.handlePrevBTn}>&larr; Previous</button>
                <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize)} type="button" className="btn btn-dark" onClick={this.handleNextBTn}>Next &rarr; </button>
            </div>
        </div>
      
    );
  }
}
