import React, { Component } from 'react'

export default class NewsItem extends Component {
  render() {
    let {title, description,imageUrl,newsurl,author,date,source} = this.props;
    return ( 
      <div className='my-3'>
        <div className="card" >
        <span class="position-absolute top-0 translate-middle badge rounded-pill bg-danger" style={{zIndex : '1',left : '90%'}}>
            {source}
        </span>
            <img src={imageUrl ? imageUrl : "https://www.shutterstock.com/image-photo/tablet-news-website-on-stack-newspapers-1059533777"}
             className="card-img-top" alt={"..."}/>
                <div className="card-body">
                    <h5 className="card-title">{title}...</h5>
                    <p className="card-text">{description}...</p>
                    <p className="card-text"><small className="text-muted">by {author? author : "unknown"} on {new Date(date).toGMTString()}</small></p>
                    <a href={newsurl} target="_blank" rel="noreferrer" className="btn btn-primary">Read More</a>
                </div>
        </div>
      </div>
    )
  }
}
