import React, { Component } from "react";
import NewsItem from "./NewsItem";

export default class News extends Component {
    constructor(){
        super();
        this.state ={
            articles : [],
            loading : false,
            page : 1
        }
    }

   async componentDidMount(){
        let url ="https://newsapi.org/v2/top-headlines?country=in&apiKey=4286c5491ec44d3d8502db05fe5d246c&pageSize=20";
        let data = await fetch(url);
        let parsedata = await data.json();
        console.log(parsedata);
        this.setState({articles : parsedata.articles,
            totalResults : parsedata.totalResults
        })
    }

    handlePrevBTn = async()=>{
        let url =`https://newsapi.org/v2/top-headlines?country=in&apiKey=4286c5491ec44d3d8502db05fe5d246c&page=${this.state.page - 1}&pageSize=20`;
        let data = await fetch(url);
        let parsedata = await data.json();
        console.log(parsedata);
        this.setState({articles : parsedata.articles,
                       page : this.state.page - 1
        
        })
}
    handleNextBTn = async()=>{
        if (this.state.page + 1 > Math.ceil(this.state.totalResults/20)){

        }
        else{
            let url =`https://newsapi.org/v2/top-headlines?country=in&apiKey=4286c5491ec44d3d8502db05fe5d246c&page=${this.state.page + 1}&pageSize=20`;
        let data = await fetch(url);
        let parsedata = await data.json();
        console.log(parsedata);
        this.setState({articles : parsedata.articles,
                       page : this.state.page + 1
        }) 
    }
}

  render() {
    return (
        <div className="container my-3">
            <h1>NewsMonkey - Top Headlines</h1>
                <div className="row">
                    {this.state.articles.map((element)=>{
                    return  <div className="col-md-4" key={element.url}>
                    <NewsItem title={element.title} description={element.description} imageUrl={element.urlToImage} newsurl={element.url}/>
                    </div>
                    })};
                </div>
           <div className="container d-flex justify-content-between">
                <button disabled={this.state.page<=1} type="button" class="btn btn-dark" onClick={this.handlePrevBTn}>&larr; Previous</button>
                <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults/20)} type="button" class="btn btn-dark" onClick={this.handleNextBTn}>Next &rarr; </button>
            </div>
        </div>
      
    );
  }
}
