import React, { Component } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";
import PropTypes from "prop-types";
import InfiniteScroll from "react-infinite-scroll-component";

export default class News extends Component {
  static defaultProps = {
    contry: "in",
    pageSize: "6",
    category: "general",
  };

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
  };

  Capatalixe = (string)=>{
    return string.charAt(0).toUpperCase() + string.slice(1);
  } 

  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      loading: false,
      page: 1,
    };
    document.title = `NewsMonkey - ${this.Capatalixe(this.props.category)}`;
  }

   async UpdateNews(){
     const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=4286c5491ec44d3d8502db05fe5d246c&page=${this.state.page}&pageSize=${this.props.pageSize}`;
     this.setState({ loading: true });
     let data = await fetch(url);
     let parsedata = await data.json();
     this.setState({
       articles: parsedata.articles,
       totalResults: parsedata.totalResults,
       loading: false,
     });
   }
  async componentDidMount() {
    //   let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=4286c5491ec44d3d8502db05fe5d246c&page=1&pageSize=${this.props.pageSize}`;
    //   this.setState({ loading: true });
    //   let data = await fetch(url);
    //   let parsedata = await data.json();
    //   console.log(parsedata);
    //   this.setState({
    //     articles: parsedata.articles,
    //     totalResults: parsedata.totalResults,
    //     loading: false,
    //   });
        this.UpdateNews();
  }

  handlePrevBTn = async () => {
    //  let url = `https://newsapi.org/v2/top-headlines?country=${
    //    this.props.country
    //  }&category=${
    //    this.props.category
    //  }&apiKey=4286c5491ec44d3d8502db05fe5d246c&page=${
    //    this.state.page - 1
    //  }&pageSize=${this.props.pageSize}`;
    //  this.setState({ loading: true });
    //  let data = await fetch(url);
    //  let parsedata = await data.json();
    //  console.log(parsedata);
    //  this.setState({
    //    articles: parsedata.articles,
    //    page: this.state.page - 1,
    //    loading: false,
    //  });
    this.UpdateNews();  
    this.setState({ page : this.state.page - 1});
              
  };
  handleNextBTn = async () => {
    //  if (
    //    !(
    //      this.state.page + 1 >
    //      Math.ceil(this.state.totalResults / this.props.pageSize)
    //    )
    //  )
    //    this.setState({ loading: true });
    //  let url = `https://newsapi.org/v2/top-headlines?country=${
    //    this.props.country
    //  }&category=${
    //    this.props.category
    //  }&apiKey=4286c5491ec44d3d8502db05fe5d246c&page=${
    //    this.state.page + 1
    //  }&pageSize=${this.props.pageSize}`;
    //  let data = await fetch(url);
    //  let parsedata = await data.json();
    //  console.log(parsedata);
    //  this.setState({
    //    articles: parsedata.articles,
    //    page: this.state.page + 1,
    //    loading: false,
    //  });
      this.UpdateNews();
     this.setState({ page : this.state.page + 1});
      
  };

  render() {
    return (
      <div className="container my-3">
        <h1 className="text-center" style={{ margin: "35px 0px" }}>
          NewsMonkey - Top {this.Capatalixe(this.props.category)} Headlines
        </h1>
        {/* {this.state.loading && <Spinner />} */}

        <InfiniteScroll
          dataLength={this.state.articles.length}
          next={this.fetchMoreData}
          hasMore={true}
          loader={<Spinner/>}
        >

        <div className="row">
          {this.state.articles.map((element) => {
              return (
                <div className="col-md-4" key={element.url}>
                  <NewsItem
                    title={element.title}
                    description={element.description}
                    imageUrl={element.urlToImage}
                    newsurl={element.url}
                    author={element.author}
                    date={element.publishedAt}
                    source={element.source.name}/>
                </div>
              );
            })};
        </div>
        </InfiniteScroll>

        <div className="container d-flex justify-content-between">
          <button disabled={this.state.page <= 1} type="button" className="btn btn-dark" onClick={this.handlePrevBTn}>
            &larr; Previous</button>
          <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults / this.props.pageSize)}
            type="button" className="btn btn-dark" onClick={this.handleNextBTn} > Next &rarr;{" "} </button>
        </div>
      </div>
    );
  }
}
